sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
		g_form.setSectionDisplay('x12_214', false);
        g_form.setSectionDisplay('cargo_impv7', false);
        return;
    }
	//Created by : Pruthav Abhyankar
    //Purpose : Make X12 214 and cargo IMP V7 sections based on statuses value
    if (newValue == 'x12 214v4010') {
        g_form.setSectionDisplay('x12_214', true);
		g_form.setSectionDisplay('cargo_impv7', false);


    } else if (newValue == 'cargo imp v7') {
        g_form.setSectionDisplay('cargo_impv7', true);
		g_form.setSectionDisplay('x12_214', false);

    } else {
        g_form.setSectionDisplay('x12_214', false);
        g_form.setSectionDisplay('cargo_impv7', false);
    }

}
